import React, { useState, useEffect } from 'react'
import axios from 'axios'

const CountrieCandidate = ({countrie, applyFilter}) => {
  const handleClick = () => {
    applyFilter(countrie);
  }
  return (
    <div>
      {countrie}
      <input type="button" value="show" onClick={handleClick} />
    </div>
  );
}

const Display = ({countries, applyFilter}) => {
  console.log(countries);
  if (countries.length > 10) {
    return (
      <div>Too many matches, specify another filter</div>
    );
  }
  else if (countries.length > 1) {
    return (
      <div>
        {countries.map((countrie) => <CountrieCandidate countrie={countrie.name} applyFilter={applyFilter} key={countrie.name} />)}
      </div>
    );
  }
  else if (countries.length === 1) {
    return (
      <div>
        {countries.map((countrie) => <CountrieCandidate countrie={countrie.name} applyFilter={applyFilter} key={countrie.name} />)}
        <img src={countries[0].flag} alt="flag picture" />
      </div>
    );
  }
  else {
    return (
      <div>oops nothing matched</div>
    );
  }
}

const App = () => {
  console.log("key", process.env.REACT_APP_API_KEY);
  const [newFilter, setFilter] = useState("");
  const [countries, setCountries] = useState([]);
  const [filteredCountries, setFilteredCountries] = useState([]);

  const applyFilter = (filter) => {
    setFilter(filter);
    setFilteredCountries(countries.filter((countrie) => countrie.name.toLowerCase().startsWith(filter.toLowerCase())));
  }

  const onChange = (event) => {
    const filter = event.target.value;
    applyFilter(filter);
  }

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data);
        setFilteredCountries(response.data);
      });
  }, []);


  return (
    <div>
      <div>find countries <input value={newFilter} onChange={onChange} /></div>
      <Display countries={filteredCountries} applyFilter={applyFilter} />
    </div>
  )
}

export default App
